package com.String;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1="Annaladasu";
		String s2="Jyothsna";
		String s3="Apple";
		System.out.println(s1.concat(s2));//concat() Method 
		System.out.println(s1.substring(6,10));
		System.out.println(s2.substring(1));//if there is no second argument it will go to the end of the string
		
System.out.println(s3.substring(0,2));
	}

}
